"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../context/AuthContext"

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    userType: "buyer",
  })
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const { register } = useAuth()
  const navigate = useNavigate()

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    const result = await register(formData.name, formData.email, formData.password, formData.userType)
    if (result.success) {
      navigate("/")
    } else {
      setError(result.message)
    }
    setLoading(false)
  }

  return (
    <div
      style={{ minHeight: "100vh", display: "flex", alignItems: "center", justifyContent: "center", padding: "2rem" }}
    >
      <div style={{ background: "rgba(255, 255, 255, 0.1)", padding: "2rem", borderRadius: "10px", width: "400px" }}>
        <h2 style={{ color: "#fff", textAlign: "center", marginBottom: "2rem" }}>Sign Up</h2>
        {error && <div style={{ color: "red", marginBottom: "1rem", textAlign: "center" }}>{error}</div>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "1rem", marginBottom: "1rem", borderRadius: "5px", border: "none" }}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "1rem", marginBottom: "1rem", borderRadius: "5px", border: "none" }}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            style={{ width: "100%", padding: "1rem", marginBottom: "1rem", borderRadius: "5px", border: "none" }}
          />
          <select
            name="userType"
            value={formData.userType}
            onChange={handleChange}
            style={{ width: "100%", padding: "1rem", marginBottom: "1rem", borderRadius: "5px", border: "none" }}
          >
            <option value="buyer">Buyer</option>
            <option value="seller">Seller</option>
          </select>
          <button
            type="submit"
            disabled={loading}
            style={{
              width: "100%",
              padding: "1rem",
              background: "#2563EB",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            {loading ? "Creating Account..." : "Sign Up"}
          </button>
        </form>
        <p style={{ textAlign: "center", marginTop: "1rem", color: "#ccc" }}>
          Already have an account?{" "}
          <Link to="/login" style={{ color: "#2563EB" }}>
            Login
          </Link>
        </p>
      </div>
    </div>
  )
}

export default Signup
