import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import heroimg from "../assets/sell-hero.jpg"
import '../styles/Sell.css';

const Sell = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        price: '',
        location: '',
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: '',
            country: 'USA'
        },
        type: '',
        status: 'for-sale',
        bedrooms: '',
        bathrooms: '',
        area: '',
        yearBuilt: '',
        features: [],
        amenities: [],
        images: [],
        name: '',
        email: '',
        phone: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name.includes('.')) {
            // Handle nested address fields
            const [parent, child] = name.split('.');
            setFormData(prevState => ({
                ...prevState,
                [parent]: {
                    ...prevState[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    };

    const handleFeaturesChange = (e) => {
        const features = e.target.value.split(',').map(feature => feature.trim());
        setFormData(prevState => ({
            ...prevState,
            features
        }));
    };

    const handleAmenitiesChange = (e) => {
        const amenities = e.target.value.split(',').map(amenity => amenity.trim());
        setFormData(prevState => ({
            ...prevState,
            amenities
        }));
    };

    const handleImageChange = async (e) => {
        const files = Array.from(e.target.files);
        const formData = new FormData();

        files.forEach((file, index) => {
            formData.append('images', file);
        });

        try {
            const response = await fetch('http://localhost:5001/api/upload', {
                method: 'POST',
                body: formData
            });

            if (!response.ok) {
                throw new Error('Failed to upload images');
            }

            const data = await response.json();
            setFormData(prevState => ({
                ...prevState,
                images: data.imageUrls 
            }));
        } catch (error) {
            console.error('Error uploading images:', error);
            alert('Failed to upload images. Please try again.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Form submission started...');
        setLoading(true);
        setError(null);

        try {

            if (!formData.title || !formData.price || !formData.type) {
                throw new Error('Please fill in all required fields');
            }


            const propertyData = {
                title: formData.title,
                description: formData.description,
                price: Number(formData.price),
                location: formData.location,
                address: formData.address,
                type: formData.type.toLowerCase(),
                status: formData.status,
                bedrooms: Number(formData.bedrooms),
                bathrooms: Number(formData.bathrooms),
                area: Number(formData.area),
                yearBuilt: Number(formData.yearBuilt),
                features: formData.features,
                amenities: formData.amenities,
                images: formData.images,
                contactInfo: {
                    name: formData.name,
                    email: formData.email,
                    phone: formData.phone
                }
            };

            console.log('Property data being submitted:', propertyData); 

            try {

                console.log('Attempting to login...');
                const loginResponse = await fetch('http://localhost:5001/api/auth/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        email: 'admin@example.com',
                        password: 'admin123'
                    })
                });

                console.log('Login response status:', loginResponse.status);
                const loginData = await loginResponse.json();
                console.log('Login response:', loginData);

                if (!loginResponse.ok) {
                    throw new Error(loginData.message || 'Failed to authenticate');
                }

                const token = loginData.token;


                console.log('Submitting property with token...');
                const response = await fetch('http://localhost:5001/api/properties', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        ...propertyData,
                        agent: loginData.user.id,
                        owner: loginData.user.id
                    })
                });

                console.log('Property submission response status:', response.status);
                const responseData = await response.json();
                console.log('Property submission response:', responseData);

                if (!response.ok) {
                    throw new Error(responseData.message || 'Failed to create property listing');
                }


                alert('Property listing created successfully!');
                navigate('/properties');
            } catch (fetchError) {
                console.error('Detailed error:', fetchError);
                throw new Error(fetchError.message || 'Failed to submit property. Please check if the backend server is running on port 5001.');
            }
        } catch (err) {
            console.error('Form submission error:', err);
            setError(err.message || 'Failed to submit property listing');
            alert(err.message || 'Failed to submit property listing');
        } finally {
            setLoading(false);
            console.log('Form submission completed'); 
        }
    };

    const sellerBenefits = [
        {
            icon: "fa-chart-line",
            title: "Maximum Exposure",
            description: "Get your property in front of thousands of qualified buyers through our extensive network and marketing channels."
        },
        {
            icon: "fa-dollar-sign",
            title: "Best Price",
            description: "Our expert agents use market analysis and negotiation skills to ensure you get the best possible price for your property."
        },
        {
            icon: "fa-file-contract",
            title: "Easy Process",
            description: "Experience a streamlined selling process with dedicated support and clear communication at every step."
        }
    ];

    const sellingGuides = [
        {
            id: 1,
            title: "Home Staging Guide",
            description: "Professional tips to make your home more attractive to potential buyers and increase its market value.",
            icon: "fa-paint-brush"
        },
        {
            id: 2,
            title: "Pricing Strategy",
            description: "Learn how to price your home competitively based on market trends and property features.",
            icon: "fa-chart-line"
        },
        {
            id: 3,
            title: "Marketing Tips",
            description: "Discover effective ways to market your property and reach the right audience.",
            icon: "fa-bullhorn"
        }
    ];

    const featuredListings = [
        {
            id: 1,
            title: "Spacious Family Home",
            price: 750000,
            location: "Suburban Area",
            image: IMAGES.FAMILY_HOME,
            features: ["4 Beds", "3 Baths", "3,200 sqft"]
        },
        {
            id: 2,
            title: "Cozy Downtown Condo",
            price: 450000,
            location: "City Center",
            image: IMAGES.CONDO,
            features: ["2 Beds", "2 Baths", "1,500 sqft"]
        }
    ];

    return (
        <div className="sell-page">
            {/* Hero Section */}
            <section className="sell-hero">
                <div className="hero-overlay"></div>
                <div className="hero-content">
                    <h1>Sell Your Property with Confidence</h1>
                    <p>Reach thousands of buyers and enjoy a seamless, premium selling experience.</p>
                    <a href="#sell-form" className="cta-btn">List Your Property</a>
                </div>
            </section>

            {/* Benefits Section */}
            <section className="benefits-section">
                <div className="container">
                    <h2>Why Sell With Us?</h2>
                    <div className="benefits-grid">
                        {sellerBenefits.map((benefit, idx) => (
                            <div className="benefit-card" key={idx}>
                                <i className={`fas ${benefit.icon}`}></i>
                                <h3>{benefit.title}</h3>
                                <p>{benefit.description}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Listing Form Section */}
            <section className="listing-form" id="sell-form">
                <div className="container">
                    <h2>Property Listing Form</h2>
                    <form onSubmit={handleSubmit} className="form-grid">
                        <div className="form-group">
                            <label>Property Title</label>
                            <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="e.g. Modern 3BHK Apartment" required />
                        </div>
                        <div className="form-group">
                            <label>Location</label>
                            <input type="text" name="location" value={formData.location} onChange={handleChange} placeholder="City, State" required />
                        </div>
                        <div className="form-group">
                            <label>Price</label>
                            <input type="number" name="price" value={formData.price} onChange={handleChange} placeholder="â‚¹" required />
                        </div>
                        <div className="form-group">
                            <label>Type</label>
                            <select name="type" value={formData.type} onChange={handleChange} required>
                                <option value="">Select Type</option>
                                <option value="house">House</option>
                                <option value="apartment">Apartment</option>
                                <option value="villa">Villa</option>
                                <option value="penthouse">Penthouse</option>
                                <option value="land">Land</option>
                                <option value="cabin">Cabin</option>
                                <option value="commercial">Commercial</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <label>Bedrooms</label>
                            <input type="number" name="bedrooms" value={formData.bedrooms} onChange={handleChange} placeholder="e.g. 3" />
                        </div>
                        <div className="form-group">
                            <label>Bathrooms</label>
                            <input type="number" name="bathrooms" value={formData.bathrooms} onChange={handleChange} placeholder="e.g. 2" />
                        </div>
                        <div className="form-group">
                            <label>Area (sqft)</label>
                            <input type="number" name="area" value={formData.area} onChange={handleChange} placeholder="e.g. 1200" />
                        </div>
                        <div className="form-group">
                            <label>Year Built</label>
                            <input type="number" name="yearBuilt" value={formData.yearBuilt} onChange={handleChange} placeholder="e.g. 2015" />
                        </div>
                        <div className="form-group full-width">
                            <label>Description</label>
                            <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Describe your property..." rows={3} required></textarea>
                        </div>
                        <div className="form-group">
                            <label>Features <span className="text-xs text-gray-400">(comma separated)</span></label>
                            <input type="text" name="features" onChange={handleFeaturesChange} placeholder="e.g. Pool, Garden, Garage" />
                        </div>
                        <div className="form-group">
                            <label>Amenities <span className="text-xs text-gray-400">(comma separated)</span></label>
                            <input type="text" name="amenities" onChange={handleAmenitiesChange} placeholder="e.g. Gym, Security, Elevator" />
                        </div>
                        <div className="form-group full-width">
                            <label>Upload Images</label>
                            <input type="file" multiple name="images" onChange={handleImageChange} />
                        </div>
                        <div className="form-group">
                            <label>Your Name</label>
                            <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" />
                        </div>
                        <div className="form-group">
                            <label>Email</label>
                            <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Your Email" />
                        </div>
                        <div className="form-group">
                            <label>Phone</label>
                            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} placeholder="Your Phone" />
                        </div>
                        <button type="submit" className="submit-button" disabled={loading}>
                            {loading ? 'Listing...' : 'List Property'}
                        </button>
                        {error && <div className="form-error">{error}</div>}
                    </form>
                </div>
            </section>
        </div>
    );
};

export default Sell;
