"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import "../styles/Signup.css"

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    userType: "buyer",
    terms: false,
  })

  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const navigate = useNavigate()
  const { register } = useAuth()

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: type === "checkbox" ? checked : value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!formData.terms) {
      setError("You must agree to the terms before registering.")
      return
    }

    setLoading(true)
    setError("")

    try {
      const result = await register(formData.name, formData.email, formData.password, formData.userType)

      if (result.success) {
        // Redirect based on user type
        if (formData.userType === "buyer") {
          navigate("/buyer-dashboard")
        } else {
          navigate("/seller-dashboard")
        }
      } else {
        setError(result.message)
      }
    } catch (error) {
      setError("Something went wrong. Please try again.")
    }

    setLoading(false)
  }

  const handleGoogleLogin = () => {
    window.location.href = "/api/auth/google"
  }

  const handleGithubLogin = () => {
    window.location.href = "/api/auth/github"
  }

  const preventLink = (e) => e.preventDefault()

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1>Create an account</h1>
        <p>Enter your details below to create your account</p>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a password"
              required
              minLength={6}
            />
          </div>

          <div className="form-group">
            <label>I am a:</label>
            <div className="user-type-selector">
              <label className={`user-type-option ${formData.userType === "buyer" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="buyer"
                  checked={formData.userType === "buyer"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">🏠</div>
                  <div className="user-type-title">Buyer</div>
                  <div className="user-type-desc">Looking to buy</div>
                </div>
              </label>

              <label className={`user-type-option ${formData.userType === "seller" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="seller"
                  checked={formData.userType === "seller"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">💼</div>
                  <div className="user-type-title">Seller</div>
                  <div className="user-type-desc">Looking to sell</div>
                </div>
              </label>
            </div>
          </div>

          <div className="form-group">
            <label className="remember-me">
              <input type="checkbox" name="terms" checked={formData.terms} onChange={handleChange} required />
              <span>
                I agree to the{" "}
                <a href="#" onClick={preventLink}>
                  Terms of Service
                </a>{" "}
                and{" "}
                <a href="#" onClick={preventLink}>
                  Privacy Policy
                </a>
              </span>
            </label>
          </div>

          <button type="submit" className="signup-button" disabled={loading}>
            {loading ? "Creating Account..." : "Create account"}
          </button>

          <div className="social-login">
            <p>Or sign up using</p>
            <div className="social-buttons">
              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google" />
                Google
              </button>
              <button type="button" className="github-btn" onClick={handleGithubLogin}>
                <img src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png" alt="GitHub" />
                GitHub
              </button>
            </div>
          </div>
        </form>

        <div className="login-link">
          <p>
            Already have an account? <Link to="/login">Sign in</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Signup
