import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import heroimg from "../assets/sell-hero.jpg"

// import '../styles/Sell.css';

const Sell = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        price: '',
        location: '',
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: '',
            country: 'USA'
        },
        type: '',
        status: 'for-sale',
        bedrooms: '',
        bathrooms: '',
        area: '',
        yearBuilt: '',
        features: [],
        amenities: [],
        images: [],
        name: '',
        email: '',
        phone: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name.includes('.')) {
            // Handle nested address fields
            const [parent, child] = name.split('.');
            setFormData(prevState => ({
                ...prevState,
                [parent]: {
                    ...prevState[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    };

    const handleFeaturesChange = (e) => {
        const features = e.target.value.split(',').map(feature => feature.trim());
        setFormData(prevState => ({
            ...prevState,
            features
        }));
    };

    const handleAmenitiesChange = (e) => {
        const amenities = e.target.value.split(',').map(amenity => amenity.trim());
        setFormData(prevState => ({
            ...prevState,
            amenities
        }));
    };

    const handleImageChange = async (e) => {
        const files = Array.from(e.target.files);
        const formData = new FormData();

        files.forEach((file, index) => {
            formData.append('images', file);
        });

        try {
            const response = await fetch('http://localhost:5001/api/upload', {
                method: 'POST',
                body: formData
            });

            if (!response.ok) {
                throw new Error('Failed to upload images');
            }

            const data = await response.json();
            setFormData(prevState => ({
                ...prevState,
                images: data.imageUrls 
            }));
        } catch (error) {
            console.error('Error uploading images:', error);
            alert('Failed to upload images. Please try again.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Form submission started...');
        setLoading(true);
        setError(null);

        try {

            if (!formData.title || !formData.price || !formData.type) {
                throw new Error('Please fill in all required fields');
            }


            const propertyData = {
                title: formData.title,
                description: formData.description,
                price: Number(formData.price),
                location: formData.location,
                address: formData.address,
                type: formData.type.toLowerCase(),
                status: formData.status,
                bedrooms: Number(formData.bedrooms),
                bathrooms: Number(formData.bathrooms),
                area: Number(formData.area),
                yearBuilt: Number(formData.yearBuilt),
                features: formData.features,
                amenities: formData.amenities,
                images: formData.images,
                contactInfo: {
                    name: formData.name,
                    email: formData.email,
                    phone: formData.phone
                }
            };

            console.log('Property data being submitted:', propertyData); 

            try {

                console.log('Attempting to login...');
                const loginResponse = await fetch('http://localhost:5001/api/auth/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        email: 'admin@example.com',
                        password: 'admin123'
                    })
                });

                console.log('Login response status:', loginResponse.status);
                const loginData = await loginResponse.json();
                console.log('Login response:', loginData);

                if (!loginResponse.ok) {
                    throw new Error(loginData.message || 'Failed to authenticate');
                }

                const token = loginData.token;


                console.log('Submitting property with token...');
                const response = await fetch('http://localhost:5001/api/properties', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        ...propertyData,
                        agent: loginData.user.id,
                        owner: loginData.user.id
                    })
                });

                console.log('Property submission response status:', response.status);
                const responseData = await response.json();
                console.log('Property submission response:', responseData);

                if (!response.ok) {
                    throw new Error(responseData.message || 'Failed to create property listing');
                }


                alert('Property listing created successfully!');
                navigate('/properties');
            } catch (fetchError) {
                console.error('Detailed error:', fetchError);
                throw new Error(fetchError.message || 'Failed to submit property. Please check if the backend server is running on port 5001.');
            }
        } catch (err) {
            console.error('Form submission error:', err);
            setError(err.message || 'Failed to submit property listing');
            alert(err.message || 'Failed to submit property listing');
        } finally {
            setLoading(false);
            console.log('Form submission completed'); 
        }
    };

    const sellerBenefits = [
        {
            icon: "fa-chart-line",
            title: "Maximum Exposure",
            description: "Get your property in front of thousands of qualified buyers through our extensive network and marketing channels."
        },
        {
            icon: "fa-dollar-sign",
            title: "Best Price",
            description: "Our expert agents use market analysis and negotiation skills to ensure you get the best possible price for your property."
        },
        {
            icon: "fa-file-contract",
            title: "Easy Process",
            description: "Experience a streamlined selling process with dedicated support and clear communication at every step."
        }
    ];

    const sellingGuides = [
        {
            id: 1,
            title: "Home Staging Guide",
            description: "Professional tips to make your home more attractive to potential buyers and increase its market value.",
            icon: "fa-paint-brush"
        },
        {
            id: 2,
            title: "Pricing Strategy",
            description: "Learn how to price your home competitively based on market trends and property features.",
            icon: "fa-chart-line"
        },
        {
            id: 3,
            title: "Marketing Tips",
            description: "Discover effective ways to market your property and reach the right audience.",
            icon: "fa-bullhorn"
        }
    ];

    const featuredListings = [
        {
            id: 1,
            title: "Spacious Family Home",
            price: 750000,
            location: "Suburban Area",
            image: IMAGES.FAMILY_HOME,
            features: ["4 Beds", "3 Baths", "3,200 sqft"]
        },
        {
            id: 2,
            title: "Cozy Downtown Condo",
            price: 450000,
            location: "City Center",
            image: IMAGES.CONDO,
            features: ["2 Beds", "2 Baths", "1,500 sqft"]
        }
    ];

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white flex items-center justify-center font-sans">
            <div className="w-full max-w-2xl bg-white rounded-3xl shadow-2xl p-10">
                <h1 className="text-4xl font-extrabold text-blue-700 mb-2 text-center">List Your Property</h1>
                <p className="text-lg text-gray-500 mb-8 text-center">
                    Reach thousands of buyers with a premium listing experience.
                </p>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label className="block text-gray-700 font-semibold mb-2">Title</label>
                            <input
                                type="text"
                                className="w-full border border-gray-200 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-400 transition"
                                placeholder="Modern 3BHK Apartment"
                                name="title"
                                value={formData.title}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label className="block text-gray-700 font-semibold mb-2">Location</label>
                            <input
                                type="text"
                                className="w-full border border-gray-200 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-400 transition"
                                placeholder="City, State"
                                name="location"
                                value={formData.location}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label className="block text-gray-700 font-semibold mb-2">Price</label>
                            <input
                                type="number"
                                className="w-full border border-gray-200 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-400 transition"
                                placeholder="₹"
                                name="price"
                                value={formData.price}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label className="block text-gray-700 font-semibold mb-2">Type</label>
                            <select
                                className="w-full border border-gray-200 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-400 transition"
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Select Type</option>
                                <option value="apartment">Apartment</option>
                                <option value="villa">Villa</option>
                                <option value="house">House</option>
                                <option value="penthouse">Penthouse</option>
                                <option value="land">Land</option>
                            </select>
                        </div>
                    </div>
                    <div>
                        <label className="block text-gray-700 font-semibold mb-2">Description</label>
                        <textarea
                            className="w-full border border-gray-200 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-400 transition"
                            rows={4}
                            placeholder="Describe your property..."
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            required
                        ></textarea>
                    </div>
                    <div>
                        <label className="block text-gray-700 font-semibold mb-2">Upload Images</label>
                        <input
                            type="file"
                            multiple
                            className="w-full"
                            name="images"
                            onChange={handleImageChange}
                        />
                    </div>
                    <button type="submit" className="w-full bg-gradient-to-r from-blue-600 to-blue-400 text-white font-bold py-3 rounded-xl shadow-lg hover:from-blue-700 hover:to-blue-500 transition text-lg mt-4">
                        List Property
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Sell;
