import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { Link } from "react-router-dom";

const Properties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filters, setFilters] = useState({
    type: "all",
    status: "all",
    minPrice: "",
    maxPrice: "",
    bedrooms: "",
    bathrooms: "",
    location: "",
  });

  const { user, isAuthenticated } = useAuth();

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams({
        sort: "-createdAt", // Sort by newest first
      });

      // Add filters to query params
      Object.entries(filters).forEach(([key, value]) => {
        if (value && value !== "all") {
          queryParams.append(key, value);
        }
      });

      const response = await fetch(`http://localhost:5001/api/properties?${queryParams}`);
      if (response.ok) {
        const data = await response.json();
        setProperties(data.properties || []);
      } else {
        setError("Failed to fetch properties");
      }
    } catch (error) {
      console.error("Error fetching properties:", error);
      setError("Error loading properties");
    } finally {
      setLoading(false);
    }
  };

  // Initial fetch
  useEffect(() => {
    fetchProperties();
  }, [filters]);

  // Set up periodic refresh
  useEffect(() => {
    const refreshInterval = setInterval(fetchProperties, 30000); // Refresh every 30 seconds
    return () => clearInterval(refreshInterval);
  }, [filters]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  if (loading) {
    return <div className="loading">Loading properties...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="properties-page">
      {/* Search Section */}
      <section className="search-section">
        <div className="search-container">
          <h1>Find Your Perfect Property</h1>
          <div className="filters">
            <select value={filters.type} onChange={(e) => handleFilterChange("type", e.target.value)}>
              <option value="all">All Types</option>
              <option value="house">Houses</option>
              <option value="apartment">Apartments</option>
              <option value="villa">Villas</option>
              <option value="land">Land</option>
            </select>

            <select value={filters.status} onChange={(e) => handleFilterChange("status", e.target.value)}>
              <option value="all">All Status</option>
              <option value="for-sale">For Sale</option>
              <option value="for-rent">For Rent</option>
            </select>

            <input
              type="text"
              placeholder="Location..."
              value={filters.location}
              onChange={(e) => handleFilterChange("location", e.target.value)}
            />

            <select value={filters.bedrooms} onChange={(e) => handleFilterChange("bedrooms", e.target.value)}>
              <option value="">Any Bedrooms</option>
              <option value="1">1+ Bedrooms</option>
              <option value="2">2+ Bedrooms</option>
              <option value="3">3+ Bedrooms</option>
              <option value="4">4+ Bedrooms</option>
            </select>
          </div>
        </div>
      </section>

      {/* Properties Grid */}
      <section className="properties-section">
        <div className="container">
          {properties.length === 0 ? (
            <div className="no-properties">
              <h3>No properties found</h3>
              <p>Try adjusting your search filters</p>
            </div>
          ) : (
            <div className="properties-grid">
              {properties.map((property) => (
                <div key={property._id} className="property-card">
                  <div className="property-image">
                    <img
                      src={property.images?.[0] ? `http://localhost:5001${property.images[0]}` : "/placeholder.svg?height=250&width=350"}
                      alt={property.title}
                      onError={(e) => {
                        e.target.src = "/placeholder.svg?height=250&width=350";
                      }}
                    />
                    <div className="property-price">â‚¹{property.price.toLocaleString()}</div>
                    <div className="property-type">{property.type}</div>
                  </div>
                  <div className="property-details">
                    <h3>{property.title}</h3>
                    <div className="location">
                      <i className="fas fa-map-marker-alt"></i>
                      <span>{property.location}</span>
                    </div>
                    <div className="property-features">
                      <span>
                        <i className="fas fa-bed"></i>
                        {property.bedrooms} Beds
                      </span>
                      <span>
                        <i className="fas fa-bath"></i>
                        {property.bathrooms} Baths
                      </span>
                      <span>
                        <i className="fas fa-ruler-combined"></i>
                        {property.area} sqft
                      </span>
                    </div>
                    <Link to={`/property/${property._id}`} className="view-property">
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Properties
