"use client"

import { useState } from "react"
import { Link, useNavigate, useLocation } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import "../styles/styles.css"

const Signup = () => {
  const location = useLocation()
  const navigate = useNavigate()
  const { register } = useAuth()

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    userType: "buyer",
    terms: false,
  })

  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  // Check if we're in OAuth user type selection mode
  const queryParams = new URLSearchParams(location.search)
  const token = queryParams.get("token")
  const needsUserType = queryParams.get("needsUserType") === "true"
  const isOAuthFlow = token && needsUserType

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === "checkbox" ? checked : value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    if (!formData.terms) {
      setError("You must agree to the terms before registering.")
      return
    }

    setLoading(true)

    try {
      // Regular signup flow
      const result = await register(formData.name, formData.email, formData.password, formData.userType)

      if (result.success) {
        navigate("/")
      } else {
        setError(result.message || "Registration failed")
      }
    } catch (error) {
      setError(error.response?.data?.message || "Something went wrong!")
    } finally {
      setLoading(false)
    }
  }

  const handleOAuthUserTypeSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)

    try {
      // Update user type for OAuth users
      const response = await fetch(`${process.env.REACT_APP_API_URL || ""}/api/auth/update-user-type`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "x-auth-token": token,
        },
        body: JSON.stringify({ userType: formData.userType }),
      })

      const data = await response.json()

      if (response.ok) {
        // Store token and redirect
        localStorage.setItem("token", token)
        localStorage.setItem("user", JSON.stringify(data.user))
        navigate("/")
      } else {
        setError(data.message || "Failed to update user type")
      }
    } catch (error) {
      setError("Something went wrong!")
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleLogin = () => {
    window.location.href = "/api/auth/google"
  }

  const handleGithubLogin = () => {
    window.location.href = "/api/auth/github"
  }

  const preventLink = (e) => e.preventDefault()

  // Render OAuth user type selection form
  if (isOAuthFlow) {
    return (
      <div className="container">
        <div className="login-container">
          <div className="login-header">
            <h1>Complete Your Registration</h1>
            <p>Please select your account type to continue</p>
          </div>

          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleOAuthUserTypeSubmit}>
            <div className="form-group">
              <label>I am a:</label>
              <div className="user-type-options">
                <label className={`user-type-option ${formData.userType === "buyer" ? "selected" : ""}`}>
                  <input
                    type="radio"
                    name="userType"
                    value="buyer"
                    checked={formData.userType === "buyer"}
                    onChange={handleChange}
                  />
                  <div className="user-type-content">
                    <div className="user-type-icon">🏠</div>
                    <div className="user-type-label">Buyer</div>
                  </div>
                </label>

                <label className={`user-type-option ${formData.userType === "seller" ? "selected" : ""}`}>
                  <input
                    type="radio"
                    name="userType"
                    value="seller"
                    checked={formData.userType === "seller"}
                    onChange={handleChange}
                  />
                  <div className="user-type-content">
                    <div className="user-type-icon">💼</div>
                    <div className="user-type-label">Seller</div>
                  </div>
                </label>
              </div>
            </div>

            <button type="submit" className="sign-in-btn" disabled={loading}>
              {loading ? "Completing Registration..." : "Complete Registration"}
            </button>
          </form>
        </div>
      </div>
    )
  }

  // Regular signup form
  return (
    <div className="container">
      <div className="login-container">
        <div className="login-header">
          <h1>Create an account</h1>
          <p>Enter your email below to create your account</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a password"
              required
              minLength={6}
            />
          </div>

          <div className="form-group">
            <label>I am a:</label>
            <div className="user-type-options">
              <label className={`user-type-option ${formData.userType === "buyer" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="buyer"
                  checked={formData.userType === "buyer"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">🏠</div>
                  <div className="user-type-label">Buyer</div>
                </div>
              </label>

              <label className={`user-type-option ${formData.userType === "seller" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="seller"
                  checked={formData.userType === "seller"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">💼</div>
                  <div className="user-type-label">Seller</div>
                </div>
              </label>
            </div>
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                id="terms"
                name="terms"
                checked={formData.terms}
                onChange={handleChange}
                required
              />
              <span>
                I agree to the{" "}
                <a href="#" onClick={preventLink}>
                  Terms of Service
                </a>{" "}
                and{" "}
                <a href="#" onClick={preventLink}>
                  Privacy Policy
                </a>
              </span>
            </label>
          </div>

          <button type="submit" className="sign-in-btn" disabled={loading}>
            {loading ? "Creating Account..." : "Create account"}
          </button>

          <div className="social-login">
            <p>Or sign up using</p>
            <div className="social-buttons">
              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google" />
                Google
              </button>
              <button type="button" className="github-btn" onClick={handleGithubLogin}>
                <img src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png" alt="GitHub" />
                GitHub
              </button>
            </div>
          </div>
        </form>

        <div className="sign-up-link">
          <p>
            Already have an account? <Link to="/login">Sign in</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Signup
