import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";

const Properties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filters, setFilters] = useState({
    type: "all",
    status: "all",
    minPrice: "",
    maxPrice: "",
    bedrooms: "",
    bathrooms: "",
    location: "",
  });

  const { user, isAuthenticated } = useAuth();

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams({
        sort: "-createdAt", // Sort by newest first
        ...filters
      });

      const response = await fetch(`http://localhost:5001/api/properties?${queryParams}`);
      if (response.ok) {
        const data = await response.json();
        setProperties(data.properties || []);
      } else {
        setError("Failed to fetch properties");
      }
    } catch (error) {
      console.error("Error fetching properties:", error);
      setError("Error loading properties");
    } finally {
      setLoading(false);
    }
  };

  // Initial fetch
  useEffect(() => {
    fetchProperties();
  }, [filters]);

  // Set up periodic refresh
  useEffect(() => {
    const refreshInterval = setInterval(fetchProperties, 30000); // Refresh every 30 seconds
    return () => clearInterval(refreshInterval);
  }, [filters]);

  return (
    <div style={{ minHeight: "100vh", paddingTop: "80px", padding: "2rem" }}>
      <h1 style={{ color: "#fff", textAlign: "center" }}>All Properties</h1>
      <p style={{ color: "#ccc", textAlign: "center" }}>Browse all available properties.</p>
    </div>
  )
}

export default Properties
