"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import { useNavigate } from "react-router-dom"
import "../styles/Signup.css"

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    terms: false,
    userType: "buyer",
  })

  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const { register } = useAuth()
  const navigate = useNavigate()

  const [showUserTypeSelection, setShowUserTypeSelection] = useState(false)
  const [oauthToken, setOauthToken] = useState(null)

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search)
    const token = urlParams.get("token")
    const needsUserType = urlParams.get("needsUserType")

    if (token && needsUserType === "true") {
      setOauthToken(token)
      setShowUserTypeSelection(true)
    }
  }, [])

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === "checkbox" ? checked : value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    if (!formData.terms) {
      setError("You must agree to the terms before registering.")
      return
    }

    setLoading(true)

    try {
      const result = await register(formData.name, formData.email, formData.password, formData.userType)

      if (result.success) {
        navigate("/")
      } else {
        setError(result.message)
      }
    } catch (error) {
      setError(error.response?.data?.message || "Something went wrong!")
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleLogin = () => {
    // Add state parameter to indicate this is a signup flow
    window.location.href = `${process.env.REACT_APP_API_URL || "http://localhost:5001"}/api/auth/google?signup=true`
  }

  const handleGithubLogin = () => {
    // Add state parameter to indicate this is a signup flow
    window.location.href = `${process.env.REACT_APP_API_URL || "http://localhost:5001"}/api/auth/github?signup=true`
  }

  const handleOAuthUserTypeSelection = async () => {
    if (!oauthToken || !formData.userType) return

    setLoading(true)
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL || "http://localhost:5001"}/api/auth/update-user-type`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "x-auth-token": oauthToken,
          },
          body: JSON.stringify({ userType: formData.userType }),
        },
      )

      const data = await response.json()

      if (response.ok) {
        // Store token and user data
        localStorage.setItem("token", oauthToken)
        localStorage.setItem("user", JSON.stringify(data.user))
        navigate("/")
      } else {
        setError(data.message || "Failed to update user type")
      }
    } catch (error) {
      setError("Something went wrong!")
    } finally {
      setLoading(false)
    }
  }

  const preventLink = (e) => e.preventDefault()

  if (showUserTypeSelection) {
    return (
      <div className="container">
        <div className="login-container">
          <div className="login-header">
            <h1>Complete Your Registration</h1>
            <p>Please select your account type to continue</p>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="user-type-selection">
            <div className="form-group">
              <label>I am a:</label>
              <div className="user-type-selector">
                <label className={`user-type-option ${formData.userType === "buyer" ? "selected" : ""}`}>
                  <input
                    type="radio"
                    name="userType"
                    value="buyer"
                    checked={formData.userType === "buyer"}
                    onChange={handleChange}
                  />
                  <div className="user-type-content">
                    <div className="user-type-icon">üè†</div>
                    <div className="user-type-title">Buyer</div>
                    <div className="user-type-desc">Looking to buy properties</div>
                  </div>
                </label>

                <label className={`user-type-option ${formData.userType === "seller" ? "selected" : ""}`}>
                  <input
                    type="radio"
                    name="userType"
                    value="seller"
                    checked={formData.userType === "seller"}
                    onChange={handleChange}
                  />
                  <div className="user-type-content">
                    <div className="user-type-icon">üíº</div>
                    <div className="user-type-title">Seller</div>
                    <div className="user-type-desc">Looking to sell properties</div>
                  </div>
                </label>
              </div>
            </div>

            <button type="button" className="sign-in-btn" onClick={handleOAuthUserTypeSelection} disabled={loading}>
              {loading ? "Completing Registration..." : "Complete Registration"}
            </button>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="container">
      <div className="login-container">
        <div className="login-header">
          <h1>Create an account</h1>
          <p>Enter your details below to create your account</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a password"
              required
              minLength={6}
            />
          </div>

          <div className="form-group">
            <label>I am a:</label>
            <div className="user-type-selector">
              <label className={`user-type-option ${formData.userType === "buyer" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="buyer"
                  checked={formData.userType === "buyer"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">üè†</div>
                  <div className="user-type-title">Buyer</div>
                  <div className="user-type-desc">Looking to buy</div>
                </div>
              </label>

              <label className={`user-type-option ${formData.userType === "seller" ? "selected" : ""}`}>
                <input
                  type="radio"
                  name="userType"
                  value="seller"
                  checked={formData.userType === "seller"}
                  onChange={handleChange}
                />
                <div className="user-type-content">
                  <div className="user-type-icon">üíº</div>
                  <div className="user-type-title">Seller</div>
                  <div className="user-type-desc">Looking to sell</div>
                </div>
              </label>
            </div>
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                id="terms"
                name="terms"
                checked={formData.terms}
                onChange={handleChange}
                required
              />
              <span>
                I agree to the{" "}
                <a href="#" onClick={preventLink}>
                  Terms of Service
                </a>{" "}
                and{" "}
                <a href="#" onClick={preventLink}>
                  Privacy Policy
                </a>
              </span>
            </label>
          </div>

          <button type="submit" className="sign-in-btn" disabled={loading}>
            {loading ? "Creating Account..." : "Create account"}
          </button>

          <div className="social-login">
            <p>Or sign up using</p>
            <div className="social-buttons">
              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google" />
                Google
              </button>
              <button type="button" className="github-btn" onClick={handleGithubLogin}>
                <img src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png" alt="GitHub" />
                GitHub
              </button>
            </div>
          </div>
        </form>

        <div className="sign-up-link">
          <p>
            Already have an account? <Link to="/login">Sign in</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Signup
