import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import '../styles/Navbar.css';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 20);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, [location]); // Re-check login status when route changes

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setIsLoggedIn(false);
        navigate('/login');
    };

    const isActive = (path) => location.pathname === path;

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <div className="nav-left">
                <Link to="/" className="logo">
                    <img src={IMAGES.LOGO} alt="REALTOR" />
                    <span>REALTOR</span>
                </Link>
            </div>

            <div className="nav-center">
                <Link to="/" className={`nav-link ${isActive('/') ? 'active' : ''}`}>Home</Link>
                <Link to="/properties" className={`nav-link ${isActive('/properties') ? 'active' : ''}`}>Properties</Link>
                <Link to="/buy" className={`nav-link ${isActive('/buy') ? 'active' : ''}`}>Buy</Link>
                <Link to="/sell" className={`nav-link ${isActive('/sell') ? 'active' : ''}`}>Sell</Link>
                <Link to="/rent" className={`nav-link ${isActive('/rent') ? 'active' : ''}`}>Rent</Link>
            </div>

            <div className="nav-right">
                {isLoggedIn ? (
                    <button className="logout-btn" onClick={handleLogout}>
                        Logout
                    </button>
                ) : (
                    <>
                        <Link to="/login" className="login-btn">Login</Link>
                        <Link to="/signup" className="get-started-btn">Signup</Link>
                    </>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
