import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/styles.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    terms: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.terms) {
      alert("You must agree to the terms before registering.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5001/api/auth/register', {
        name: formData.name,
        email: formData.email,
        password: formData.password
      });

      alert(response.data.message || "Account created successfully!");
      setFormData({
        name: '',
        email: '',
        password: '',
        terms: false
      });
    } catch (error) {
      alert(error.response?.data?.message || 'Something went wrong!');
    }
  };

  // âœ… Updated: Working social login buttons
  const handleGoogleLogin = () => {
    window.location.href = 'http://localhost:5001/api/auth/google';
  };

  const handleGithubLogin = () => {
    window.location.href = 'http://localhost:5001/api/auth/github';
  };

  const preventLink = (e) => e.preventDefault();

  return (
    <div className="container">
      <div className="login-container">
        <div className="login-header">
          <h1>Create an account</h1>
          <p>Enter your email below to create your account</p>
        </div>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a password"
              required
              minLength={6}
            />
          </div>
          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                id="terms"
                name="terms"
                checked={formData.terms}
                onChange={handleChange}
                required
              />
              <span>
                I agree to the{' '}
                <a href="#" onClick={preventLink}>Terms of Service</a>{' '}
                and{' '}
                <a href="#" onClick={preventLink}>Privacy Policy</a>
              </span>
            </label>
          </div>
          <button type="submit" className="sign-in-btn">Create account</button>

          <div className="social-login">
            <p>Or sign up using</p>
            <div className="social-buttons">
              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg"
                  alt="Google"
                />
                Google
              </button>
              <button type="button" className="github-btn" onClick={handleGithubLogin}>
                <img
                  src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png"
                  alt="GitHub"
                />
                GitHub
              </button>
            </div>
          </div>
        </form>

        <div className="sign-up-link">
          <p>
            Already have an account? <Link to="/">Sign in</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Signup;
