import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from '../utils/axiosInstance';

const Login = () => {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: '', password: '' });
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('/auth/login', data);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Error');
    }
  };
  return (
    <div className="container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <input name="email" value={data.email} onChange={e => setData({ ...data, email: e.target.value })} placeholder="Email" required/>
        <input name="password" type="password" value={data.password} onChange={e => setData({ ...data, password: e.target.value })} placeholder="Password" required/>
        <button type="submit">Login</button>
      </form>
      <hr />
      <Link to="/forgot-password">Forgot your password?</Link>
      <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
    </div>
  );
};

export default Login;
