import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import '../styles/Buy.css';

const Buy = () => {
    const [filters, setFilters] = useState({
        priceRange: [0, 2000000],
        propertyType: 'all',
        bedrooms: 'all',
        bathrooms: 'all',
        location: 'all',
        area: [0, 5000]
    });

    const [tempFilters, setTempFilters] = useState({ ...filters });
    const [filteredProperties, setFilteredProperties] = useState([]);

    const buyingGuides = [
        {
            id: 1,
            title: "First-Time Home Buyer's Guide",
            description: "Everything you need to know about buying your first home",
            icon: "fa-home"
        },
        {
            id: 2,
            title: "Mortgage Calculator",
            description: "Calculate your monthly payments and interest rates",
            icon: "fa-calculator"
        },
        {
            id: 3,
            title: "Property Inspection Tips",
            description: "What to look for when inspecting a property",
            icon: "fa-search"
        }
    ];

    const properties = [
        {
            id: 1,
            title: "Luxury Beachfront Villa",
            price: 1200000,
            location: "Miami Beach, FL",
            type: "house",
            bedrooms: 5,
            bathrooms: 4,
            area: 4500,
            image: IMAGES.LUXURY_VILLA,
            features: ["5 Beds", "4 Baths", "4,500 sqft"]
        },
        {
            id: 2,
            title: "Modern City Penthouse",
            price: 850000,
            location: "Downtown LA",
            type: "condo",
            bedrooms: 3,
            bathrooms: 3,
            area: 2800,
            image: IMAGES.PENTHOUSE,
            features: ["3 Beds", "3 Baths", "2,800 sqft"]
        },
        {
            id: 3,
            title: "Suburban Family Home",
            price: 650000,
            location: "Austin, TX",
            type: "house",
            bedrooms: 4,
            bathrooms: 3,
            area: 3200,
            image: IMAGES.FAMILY_HOME,
            features: ["4 Beds", "3 Baths", "3,200 sqft"]
        }
    ];

    const handleFilterChange = (name, value) => {
        setTempFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const applyFilters = () => {
        setFilters(tempFilters);
    };

    const resetFilters = () => {
        const defaultFilters = {
            priceRange: [0, 2000000],
            propertyType: 'all',
            bedrooms: 'all',
            bathrooms: 'all',
            location: 'all',
            area: [0, 5000]
        };
        setTempFilters(defaultFilters);
        setFilters(defaultFilters);
    };

    useEffect(() => {
        const filtered = properties.filter(property => {
            // Price range filter
            if (property.price < filters.priceRange[0] || property.price > filters.priceRange[1]) {
                return false;
            }

            // Property type filter
            if (filters.propertyType !== 'all' && property.type !== filters.propertyType) {
                return false;
            }

            // Bedrooms filter
            if (filters.bedrooms !== 'all') {
                if (filters.bedrooms === '4+' && property.bedrooms < 4) {
                    return false;
                } else if (filters.bedrooms !== '4+' && property.bedrooms !== parseInt(filters.bedrooms)) {
                    return false;
                }
            }

            // Bathrooms filter
            if (filters.bathrooms !== 'all' && property.bathrooms !== parseInt(filters.bathrooms)) {
                return false;
            }

            // Area filter
            if (property.area < filters.area[0] || property.area > filters.area[1]) {
                return false;
            }

            // Location filter
            if (filters.location !== 'all') {
                const propertyLocation = property.location.toLowerCase().replace(/\s+/g, '-');
                if (propertyLocation !== filters.location) {
                    return false;
                }
            }

            return true;
        });

        setFilteredProperties(filtered);
    }, [filters]);

    return (
        <div className="buy-page">
            <section className="hero-section">
                <div className="hero-content">
                    <h1>Find Your Dream Home</h1>
                    <p>Discover the perfect property that matches your lifestyle</p>
                    <div className="hero-stats">
                        <div className="stat">
                            <span className="number">{filteredProperties.length}</span>
                            <span className="label">Properties</span>
                        </div>
                        <div className="stat">
                            <span className="number">98%</span>
                            <span className="label">Satisfaction</span>
                        </div>
                        <div className="stat">
                            <span className="number">24/7</span>
                            <span className="label">Support</span>
                        </div>
                    </div>
                </div>
            </section>

            <section className="filters-section">
                <div className="container">
                    <div className="filters-grid">
                        <div className="filter-group">
                            <label>Price Range</label>
                            <div className="price-inputs">
                                <input 
                                    type="number"
                                    value={tempFilters.priceRange[0]}
                                    onChange={(e) => handleFilterChange('priceRange', [
                                        parseInt(e.target.value) || 0,
                                        tempFilters.priceRange[1]
                                    ])}
                                    placeholder="Min"
                                />
                                <span>to</span>
                                <input 
                                    type="number"
                                    value={tempFilters.priceRange[1]}
                                    onChange={(e) => handleFilterChange('priceRange', [
                                        tempFilters.priceRange[0],
                                        parseInt(e.target.value) || 2000000
                                    ])}
                                    placeholder="Max"
                                />
                            </div>
                        </div>

                        <div className="filter-group">
                            <label>Property Type</label>
                            <select 
                                value={tempFilters.propertyType}
                                onChange={(e) => handleFilterChange('propertyType', e.target.value)}
                            >
                                <option value="all">All Types</option>
                                <option value="house">House</option>
                                <option value="apartment">Apartment</option>
                                <option value="condo">Condo</option>
                                <option value="townhouse">Townhouse</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Bedrooms</label>
                            <select 
                                value={tempFilters.bedrooms}
                                onChange={(e) => handleFilterChange('bedrooms', e.target.value)}
                            >
                                <option value="all">Any</option>
                                <option value="1">1 Bed</option>
                                <option value="2">2 Beds</option>
                                <option value="3">3 Beds</option>
                                <option value="4+">4+ Beds</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Bathrooms</label>
                            <select 
                                value={tempFilters.bathrooms}
                                onChange={(e) => handleFilterChange('bathrooms', e.target.value)}
                            >
                                <option value="all">Any</option>
                                <option value="1">1 Bath</option>
                                <option value="2">2 Baths</option>
                                <option value="3">3 Baths</option>
                                <option value="4+">4+ Baths</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Area (sq ft)</label>
                            <div className="area-inputs">
                                <input 
                                    type="number"
                                    value={tempFilters.area[0]}
                                    onChange={(e) => handleFilterChange('area', [
                                        parseInt(e.target.value) || 0,
                                        tempFilters.area[1]
                                    ])}
                                    placeholder="Min"
                                />
                                <span>to</span>
                                <input 
                                    type="number"
                                    value={tempFilters.area[1]}
                                    onChange={(e) => handleFilterChange('area', [
                                        tempFilters.area[0],
                                        parseInt(e.target.value) || 5000
                                    ])}
                                    placeholder="Max"
                                />
                            </div>
                        </div>

                        <div className="filter-group">
                            <label>Location</label>
                            <select 
                                value={tempFilters.location}
                                onChange={(e) => handleFilterChange('location', e.target.value)}
                            >
                                <option value="all">All Locations</option>
                                <option value="miami-beach-fl">Miami Beach, FL</option>
                                <option value="downtown-la">Downtown LA</option>
                                <option value="austin-tx">Austin, TX</option>
                            </select>
                        </div>
                    </div>
                    <div className="filter-actions">
                        <button className="reset-btn" onClick={resetFilters}>
                            Reset Filters
                        </button>
                        <button className="apply-btn" onClick={applyFilters}>
                            Apply Filters
                        </button>
                    </div>
                </div>
            </section>

            <section className="featured-properties">
                <div className="container">
                    <h2>Properties for Sale</h2>
                    {filteredProperties.length === 0 ? (
                        <div className="no-properties">
                            <h3>No properties found</h3>
                            <p>Try adjusting your filters or resetting them to see more properties.</p>
                            <button className="reset-btn" onClick={resetFilters}>
                                Reset Filters
                            </button>
                        </div>
                    ) : (
                        <div className="properties-grid">
                            {filteredProperties.map(property => (
                                <div key={property.id} className="property-card">
                                    <div className="property-image">
                                        <img src={property.image} alt={property.title} />
                                        <div className="property-price">
                                            ${property.price.toLocaleString()}
                                        </div>
                                    </div>
                                    <div className="property-details">
                                        <h3>{property.title}</h3>
                                        <p className="location">
                                            <i className="fas fa-map-marker-alt"></i> {property.location}
                                        </p>
                                        <div className="features">
                                            {property.features.map((feature, index) => (
                                                <span key={index}>{feature}</span>
                                            ))}
                                        </div>
                                        <Link to={`/property/${property.id}`} className="view-property">
                                            View Property
                                        </Link>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </section>

            <section className="buying-guides">
                <div className="container">
                    <h2>Buying Guides</h2>
                    <div className="guides-grid">
                        {buyingGuides.map(guide => (
                            <div key={guide.id} className="guide-card">
                                <i className={`fas ${guide.icon}`}></i>
                                <h3>{guide.title}</h3>
                                <p>{guide.description}</p>
                                <Link to={`/guide/${guide.id}`} className="read-more">
                                    Read More
                                </Link>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            <section className="why-buy">
                <div className="container">
                    <h2>Why Buy With Us?</h2>
                    <div className="benefits-grid">
                        <div className="benefit">
                            <i className="fas fa-shield-alt"></i>
                            <h3>Trusted Agents</h3>
                            <p>Work with experienced professionals who put your interests first</p>
                        </div>
                        <div className="benefit">
                            <i className="fas fa-hand-holding-usd"></i>
                            <h3>Best Deals</h3>
                            <p>Access to exclusive properties and competitive prices</p>
                        </div>
                        <div className="benefit">
                            <i className="fas fa-clock"></i>
                            <h3>24/7 Support</h3>
                            <p>Round-the-clock assistance for all your queries</p>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Buy;
