import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import '../styles/Properties.css';

const Properties = () => {
    const [selectedCity, setSelectedCity] = useState('all');
    const [selectedType, setSelectedType] = useState('all');
    const [priceRange, setPriceRange] = useState([0, 1000000]);
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProperties = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://localhost:5000/api/properties');
                if (!response.ok) {
                    throw new Error('Failed to fetch properties');
                }
                const data = await response.json();
                setProperties(data.properties);
                setError(null);
            } catch (err) {
                setError(err.message);
                console.error('Error fetching properties:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchProperties();
    }, []);

    const filteredProperties = properties.filter(property => {
        // City filter
        if (selectedCity !== 'all' && property.location.toLowerCase() !== selectedCity) {
            return false;
        }

        // Type filter
        if (selectedType !== 'all' && property.type !== selectedType) {
            return false;
        }

        // Price range filter
        if (property.price < priceRange[0] || property.price > priceRange[1]) {
            return false;
        }

        return true;
    });

    return (
        <div className="properties-page">
            <section className="search-section">
                <div className="search-container">
                    <h1>Find Your Dream Property</h1>
                    <div className="filters">
                        <select 
                            value={selectedCity} 
                            onChange={(e) => setSelectedCity(e.target.value)}
                        >
                            <option value="all">All Cities</option>
                            <option value="miami">Miami</option>
                            <option value="new york">New York</option>
                            <option value="los angeles">Los Angeles</option>
                        </select>

                        <select 
                            value={selectedType} 
                            onChange={(e) => setSelectedType(e.target.value)}
                        >
                            <option value="all">All Types</option>
                            <option value="house">House</option>
                            <option value="apartment">Apartment</option>
                            <option value="villa">Villa</option>
                        </select>

                        <div className="price-range">
                            <input 
                                type="range" 
                                min="0" 
                                max="1000000" 
                                value={priceRange[1]} 
                                onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                            />
                            <span>Max Price: ${priceRange[1].toLocaleString()}</span>
                        </div>
                    </div>
                </div>
            </section>

            <section className="properties-grid">
                {loading ? (
                    <div className="loading">Loading properties...</div>
                ) : error ? (
                    <div className="error">Error: {error}</div>
                ) : filteredProperties.length === 0 ? (
                    <div className="no-properties">No properties found matching your criteria.</div>
                ) : (
                    filteredProperties.map(property => (
                        <div key={property._id} className="property-card">
                            <div className="property-image">
                                <img src={property.images?.[0] || IMAGES.PROPERTY1} alt={property.title} />
                                <div className="property-price">
                                    ${property.price.toLocaleString()}
                                </div>
                            </div>
                            <div className="property-details">
                                <h3>{property.title}</h3>
                                <p className="location">
                                    <i className="fas fa-map-marker-alt"></i> {property.location}
                                </p>
                                <div className="property-features">
                                    <span>
                                        <i className="fas fa-bed"></i> {property.bedrooms} Beds
                                    </span>
                                    <span>
                                        <i className="fas fa-bath"></i> {property.bathrooms} Baths
                                    </span>
                                    <span>
                                        <i className="fas fa-ruler-combined"></i> {property.area} sqft
                                    </span>
                                </div>
                                <Link to={`/property/${property._id}`} className="view-details">
                                    View Details
                                </Link>
                            </div>
                        </div>
                    ))
                )}
            </section>
        </div>
    );
};

export default Properties;
