import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import '../styles/Rent.css';

const Rent = () => {
    const [filters, setFilters] = useState({
        priceRange: [0, 5000],
        propertyType: 'all',
        bedrooms: 'all',
        furnished: 'all',
        amenities: [],
        location: 'all'
    });

    const [tempFilters, setTempFilters] = useState({ ...filters });
    const [filteredProperties, setFilteredProperties] = useState([]);

    const rentalProperties = [
        {
            id: 1,
            title: "Modern Studio Apartment",
            price: 1200,
            location: "Downtown Miami",
            type: "apartment",
            bedrooms: 0,
            bathrooms: 1,
            area: "500 sqft",
            furnished: true,
            image: IMAGES.RENTAL1,
            amenities: ["Air Conditioning", "Gym", "Pool", "Parking", "Security"],
            description: "Beautiful studio apartment in the heart of downtown Miami. Perfect for young professionals or students. Features modern appliances and stunning city views.",
            rating: 4.8,
            reviews: 124,
            availableFrom: "2024-03-01",
            leaseTerm: "12 months",
            deposit: 1200,
            petsAllowed: true
        },
        {
            id: 2,
            title: "Luxury 2-Bed Condo",
            price: 2500,
            location: "South Beach",
            type: "condo",
            bedrooms: 2,
            bathrooms: 2,
            area: "1,200 sqft",
            furnished: true,
            image: IMAGES.RENTAL2,
            amenities: ["Ocean View", "Parking", "Security", "Gym", "Pool", "Concierge"],
            description: "Stunning oceanfront condo in South Beach. Features floor-to-ceiling windows, modern kitchen, and direct beach access. Perfect for those seeking luxury living.",
            rating: 4.9,
            reviews: 89,
            availableFrom: "2024-03-15",
            leaseTerm: "12 months",
            deposit: 2500,
            petsAllowed: false
        },
        {
            id: 3,
            title: "Cozy 1-Bed Apartment",
            price: 1800,
            location: "Brickell",
            type: "apartment",
            bedrooms: 1,
            bathrooms: 1,
            area: "750 sqft",
            furnished: true,
            image: IMAGES.RENTAL1,
            amenities: ["Air Conditioning", "Gym", "Pool", "Parking", "Security", "Laundry"],
            description: "Charming one-bedroom apartment in the vibrant Brickell neighborhood. Close to shopping, dining, and public transportation.",
            rating: 4.7,
            reviews: 156,
            availableFrom: "2024-04-01",
            leaseTerm: "12 months",
            deposit: 1800,
            petsAllowed: true
        }
    ];

    const handleFilterChange = (name, value) => {
        setTempFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const applyFilters = () => {
        setFilters(tempFilters);
    };

    const resetFilters = () => {
        const defaultFilters = {
            priceRange: [0, 5000],
            propertyType: 'all',
            bedrooms: 'all',
            furnished: 'all',
            amenities: [],
            location: 'all'
        };
        setTempFilters(defaultFilters);
        setFilters(defaultFilters);
    };

    useEffect(() => {
        const filtered = rentalProperties.filter(property => {
            // Price range filter
            if (property.price < filters.priceRange[0] || property.price > filters.priceRange[1]) {
                return false;
            }

            // Property type filter
            if (filters.propertyType !== 'all' && property.type !== filters.propertyType) {
                return false;
            }

            // Bedrooms filter
            if (filters.bedrooms !== 'all') {
                if (filters.bedrooms === '4+' && property.bedrooms < 4) {
                    return false;
                } else if (filters.bedrooms !== '4+' && property.bedrooms !== parseInt(filters.bedrooms)) {
                    return false;
                }
            }

            // Location filter
            if (filters.location !== 'all') {
                const propertyLocation = property.location.toLowerCase().replace(/\s+/g, '-');
                if (propertyLocation !== filters.location) {
                    return false;
                }
            }

            // Furnished filter
            if (filters.furnished !== 'all') {
                const isFurnished = filters.furnished === 'yes';
                if (property.furnished !== isFurnished) {
                    return false;
                }
            }

            return true;
        });

        setFilteredProperties(filtered);
    }, [filters]);

    return (
        <div className="rent-page">
            <section className="rent-hero">
                <div className="hero-content">
                    <h1>Find Your Perfect Rental</h1>
                    <p>Discover amazing properties for rent in prime locations across Miami</p>
                    <div className="hero-stats">
                        <div className="stat">
                            <span className="number">{filteredProperties.length}</span>
                            <span className="label">Properties</span>
                        </div>
                        <div className="stat">
                            <span className="number">98%</span>
                            <span className="label">Satisfaction</span>
                        </div>
                        <div className="stat">
                            <span className="number">24/7</span>
                            <span className="label">Support</span>
                        </div>
                    </div>
                </div>
            </section>

            <section className="filters-section">
                <div className="container">
                    <div className="filters-grid">
                        <div className="filter-group">
                            <label>Price Range</label>
                            <div className="price-inputs">
                                <input 
                                    type="number"
                                    value={tempFilters.priceRange[0]}
                                    onChange={(e) => handleFilterChange('priceRange', [
                                        parseInt(e.target.value) || 0,
                                        tempFilters.priceRange[1]
                                    ])}
                                    placeholder="Min"
                                />
                                <span>to</span>
                                <input 
                                    type="number"
                                    value={tempFilters.priceRange[1]}
                                    onChange={(e) => handleFilterChange('priceRange', [
                                        tempFilters.priceRange[0],
                                        parseInt(e.target.value) || 5000
                                    ])}
                                    placeholder="Max"
                                />
                            </div>
                        </div>

                        <div className="filter-group">
                            <label>Property Type</label>
                            <select 
                                value={tempFilters.propertyType}
                                onChange={(e) => handleFilterChange('propertyType', e.target.value)}
                            >
                                <option value="all">All Types</option>
                                <option value="apartment">Apartment</option>
                                <option value="house">House</option>
                                <option value="condo">Condo</option>
                                <option value="townhouse">Townhouse</option>
                                <option value="studio">Studio</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Bedrooms</label>
                            <select 
                                value={tempFilters.bedrooms}
                                onChange={(e) => handleFilterChange('bedrooms', e.target.value)}
                            >
                                <option value="all">Any</option>
                                <option value="0">Studio</option>
                                <option value="1">1 Bed</option>
                                <option value="2">2 Beds</option>
                                <option value="3">3 Beds</option>
                                <option value="4+">4+ Beds</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Location</label>
                            <select 
                                value={tempFilters.location}
                                onChange={(e) => handleFilterChange('location', e.target.value)}
                            >
                                <option value="all">All Locations</option>
                                <option value="downtown-miami">Downtown Miami</option>
                                <option value="brickell">Brickell</option>
                                <option value="south-beach">South Beach</option>
                                <option value="coral-gables">Coral Gables</option>
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Furnished</label>
                            <select 
                                value={tempFilters.furnished}
                                onChange={(e) => handleFilterChange('furnished', e.target.value)}
                            >
                                <option value="all">Any</option>
                                <option value="yes">Furnished</option>
                                <option value="no">Unfurnished</option>
                            </select>
                        </div>
                    </div>
                    <div className="filter-actions">
                        <button className="reset-btn" onClick={resetFilters}>
                            Reset Filters
                        </button>
                        <button className="apply-btn" onClick={applyFilters}>
                            Apply Filters
                        </button>
                    </div>
                </div>
            </section>

            <section className="properties-section">
                <div className="container">
                    {filteredProperties.length === 0 ? (
                        <div className="no-properties">
                            <h3>No properties found</h3>
                            <p>Try adjusting your filters or resetting them to see more properties.</p>
                            <button className="reset-btn" onClick={resetFilters}>
                                Reset Filters
                            </button>
                        </div>
                    ) : (
                        <div className="properties-grid">
                            {filteredProperties.map(property => (
                                <div key={property.id} className="property-card">
                                    <div className="property-image">
                                        <img src={property.image} alt={property.title} />
                                        <div className="property-price">${property.price}/mo</div>
                                    </div>
                                    <div className="property-details">
                                        <h3>{property.title}</h3>
                                        <div className="property-location">
                                            <i className="fas fa-map-marker-alt"></i>
                                            {property.location}
                                        </div>
                                        <div className="property-features">
                                            <span><i className="fas fa-bed"></i> {property.bedrooms} Beds</span>
                                            <span><i className="fas fa-bath"></i> {property.bathrooms} Baths</span>
                                            <span><i className="fas fa-ruler-combined"></i> {property.area}</span>
                                        </div>
                                        <div className="property-amenities">
                                            {property.amenities.slice(0, 3).map((amenity, index) => (
                                                <span key={index} className="amenity-tag">{amenity}</span>
                                            ))}
                                            {property.amenities.length > 3 && (
                                                <span className="amenity-tag">+{property.amenities.length - 3} more</span>
                                            )}
                                        </div>
                                        <Link to={`/property/${property.id}`} className="view-details-btn">
                                            View Details
                                        </Link>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </section>

            <section className="rental-guide">
                <div className="container">
                    <h2>Rental Guide</h2>
                    <div className="guide-grid">
                        <div className="guide-card">
                            <div className="guide-icon">📋</div>
                            <h3>Application Process</h3>
                            <p>Learn about our streamlined application process and required documents.</p>
                            <Link to="/guide/application" className="guide-link">Learn More →</Link>
                        </div>
                        <div className="guide-card">
                            <div className="guide-icon">💰</div>
                            <h3>Rental Costs</h3>
                            <p>Understand all the costs involved in renting a property.</p>
                            <Link to="/guide/costs" className="guide-link">Learn More →</Link>
                        </div>
                        <div className="guide-card">
                            <div className="guide-icon">📝</div>
                            <h3>Lease Terms</h3>
                            <p>Get familiar with our standard lease terms and conditions.</p>
                            <Link to="/guide/lease" className="guide-link">Learn More →</Link>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Rent;
