import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { IMAGES } from '../constants/assets';
import '../styles/Sell.css';

const Sell = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        price: '',
        location: '',
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: '',
            country: 'USA'
        },
        type: '',
        status: 'for-sale',
        bedrooms: '',
        bathrooms: '',
        area: '',
        yearBuilt: '',
        features: [],
        amenities: [],
        images: [],
        name: '',
        email: '',
        phone: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name.includes('.')) {
            // Handle nested address fields
            const [parent, child] = name.split('.');
            setFormData(prevState => ({
                ...prevState,
                [parent]: {
                    ...prevState[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    };

    const handleFeaturesChange = (e) => {
        const features = e.target.value.split(',').map(feature => feature.trim());
        setFormData(prevState => ({
            ...prevState,
            features
        }));
    };

    const handleAmenitiesChange = (e) => {
        const amenities = e.target.value.split(',').map(amenity => amenity.trim());
        setFormData(prevState => ({
            ...prevState,
            amenities
        }));
    };

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        // For now, we'll just store the file names
        const imageNames = files.map(file => file.name);
        setFormData(prevState => ({
            ...prevState,
            images: imageNames
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            // Format the data to match the backend schema
            const propertyData = {
                title: formData.title,
                description: formData.description,
                price: Number(formData.price),
                location: formData.location,
                address: formData.address,
                type: formData.type.toLowerCase(),
                status: formData.status,
                bedrooms: Number(formData.bedrooms),
                bathrooms: Number(formData.bathrooms),
                area: Number(formData.area),
                yearBuilt: Number(formData.yearBuilt),
                features: formData.features,
                amenities: formData.amenities,
                images: formData.images,
                // These would typically come from authentication
                agent: "user_id_here", // This should be the logged-in user's ID
                owner: "user_id_here"  // This should be the logged-in user's ID
            };

            const response = await fetch('http://localhost:5001/api/properties', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    // Add authorization header if you have authentication implemented
                    // 'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(propertyData)
            });

            if (!response.ok) {
                throw new Error('Failed to create property listing');
            }

            const result = await response.json();
            console.log('Property created:', result);
            
            // Redirect to the properties page
            navigate('/properties');
        } catch (err) {
            setError(err.message);
            console.error('Error creating property:', err);
        } finally {
            setLoading(false);
        }
    };

    const sellerBenefits = [
        {
            icon: "fa-chart-line",
            title: "Maximum Exposure",
            description: "Get your property in front of thousands of qualified buyers through our extensive network and marketing channels."
        },
        {
            icon: "fa-dollar-sign",
            title: "Best Price",
            description: "Our expert agents use market analysis and negotiation skills to ensure you get the best possible price for your property."
        },
        {
            icon: "fa-file-contract",
            title: "Easy Process",
            description: "Experience a streamlined selling process with dedicated support and clear communication at every step."
        }
    ];

    const sellingGuides = [
        {
            id: 1,
            title: "Home Staging Guide",
            description: "Professional tips to make your home more attractive to potential buyers and increase its market value.",
            icon: "fa-paint-brush"
        },
        {
            id: 2,
            title: "Pricing Strategy",
            description: "Learn how to price your home competitively based on market trends and property features.",
            icon: "fa-chart-line"
        },
        {
            id: 3,
            title: "Marketing Tips",
            description: "Discover effective ways to market your property and reach the right audience.",
            icon: "fa-bullhorn"
        }
    ];

    const featuredListings = [
        {
            id: 1,
            title: "Spacious Family Home",
            price: 750000,
            location: "Suburban Area",
            image: IMAGES.FAMILY_HOME,
            features: ["4 Beds", "3 Baths", "3,200 sqft"]
        },
        {
            id: 2,
            title: "Cozy Downtown Condo",
            price: 450000,
            location: "City Center",
            image: IMAGES.CONDO,
            features: ["2 Beds", "2 Baths", "1,500 sqft"]
        }
    ];

    return (
        <div className="sell-page">
            <section className="sell-hero">
                <div className="hero-content">
                    <h1>Sell Your Property</h1>
                    <p>List your property with us and get the best value for your home</p>
                    <Link to="#listing-form" className="cta-button">Get Started</Link>
                </div>
            </section>

            <section className="benefits-section">
                <div className="container">
                    <h2>Why Sell With Us?</h2>
                    <div className="benefits-grid">
                        {sellerBenefits.map((benefit, index) => (
                            <div key={index} className="benefit-card">
                                <i className={`fas ${benefit.icon}`}></i>
                                <h3>{benefit.title}</h3>
                                <p>{benefit.description}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            <section className="listing-form" id="listing-form">
                <div className="container">
                    <h2>List Your Property</h2>
                    {error && <div className="error-message">{error}</div>}
                    <form onSubmit={handleSubmit} className="form-grid">
                        <div className="form-group">
                            <label htmlFor="title">Property Title</label>
                            <input
                                type="text"
                                id="title"
                                name="title"
                                value={formData.title}
                                onChange={handleChange}
                                placeholder="Enter property title"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="type">Property Type</label>
                            <select
                                id="type"
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Select Property Type</option>
                                <option value="house">House</option>
                                <option value="apartment">Apartment</option>
                                <option value="condo">Condo</option>
                                <option value="townhouse">Townhouse</option>
                                <option value="land">Land</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label htmlFor="price">Price</label>
                            <input
                                type="number"
                                id="price"
                                name="price"
                                value={formData.price}
                                onChange={handleChange}
                                placeholder="Enter price"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="location">Location</label>
                            <input
                                type="text"
                                id="location"
                                name="location"
                                value={formData.location}
                                onChange={handleChange}
                                placeholder="Enter location"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address.street">Street Address</label>
                            <input
                                type="text"
                                id="address.street"
                                name="address.street"
                                value={formData.address.street}
                                onChange={handleChange}
                                placeholder="Enter street address"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address.city">City</label>
                            <input
                                type="text"
                                id="address.city"
                                name="address.city"
                                value={formData.address.city}
                                onChange={handleChange}
                                placeholder="Enter city"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address.state">State</label>
                            <input
                                type="text"
                                id="address.state"
                                name="address.state"
                                value={formData.address.state}
                                onChange={handleChange}
                                placeholder="Enter state"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address.zipCode">ZIP Code</label>
                            <input
                                type="text"
                                id="address.zipCode"
                                name="address.zipCode"
                                value={formData.address.zipCode}
                                onChange={handleChange}
                                placeholder="Enter ZIP code"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="bedrooms">Bedrooms</label>
                            <input
                                type="number"
                                id="bedrooms"
                                name="bedrooms"
                                value={formData.bedrooms}
                                onChange={handleChange}
                                placeholder="Number of bedrooms"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="bathrooms">Bathrooms</label>
                            <input
                                type="number"
                                id="bathrooms"
                                name="bathrooms"
                                value={formData.bathrooms}
                                onChange={handleChange}
                                placeholder="Number of bathrooms"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="area">Area (sq ft)</label>
                            <input
                                type="number"
                                id="area"
                                name="area"
                                value={formData.area}
                                onChange={handleChange}
                                placeholder="Property area"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="yearBuilt">Year Built</label>
                            <input
                                type="number"
                                id="yearBuilt"
                                name="yearBuilt"
                                value={formData.yearBuilt}
                                onChange={handleChange}
                                placeholder="Year built"
                                required
                            />
                        </div>

                        <div className="form-group full-width">
                            <label htmlFor="features">Features</label>
                            <input
                                type="text"
                                id="features"
                                name="features"
                                value={formData.features.join(', ')}
                                onChange={handleFeaturesChange}
                                placeholder="Enter features (comma-separated)"
                            />
                        </div>

                        <div className="form-group full-width">
                            <label htmlFor="amenities">Amenities</label>
                            <input
                                type="text"
                                id="amenities"
                                name="amenities"
                                value={formData.amenities.join(', ')}
                                onChange={handleAmenitiesChange}
                                placeholder="Enter amenities (comma-separated)"
                            />
                        </div>

                        <div className="form-group full-width">
                            <label htmlFor="description">Description</label>
                            <textarea
                                id="description"
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                placeholder="Describe your property..."
                                required
                            />
                        </div>

                        <div className="form-group full-width">
                            <label htmlFor="images">Property Images</label>
                            <input
                                type="file"
                                id="images"
                                name="images"
                                onChange={handleImageChange}
                                multiple
                                accept="image/*"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="name">Your Name</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                placeholder="Enter your name"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                placeholder="Enter your email"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone">Phone</label>
                            <input
                                type="tel"
                                id="phone"
                                name="phone"
                                value={formData.phone}
                                onChange={handleChange}
                                placeholder="Enter your phone number"
                                required
                            />
                        </div>

                        <button 
                            type="submit" 
                            className="submit-button" 
                            disabled={loading}
                        >
                            {loading ? 'Submitting...' : 'Submit Listing'}
                        </button>
                    </form>
                </div>
            </section>

            <section className="selling-guides">
                <div className="container">
                    <h2>Selling Guides</h2>
                    <div className="guides-grid">
                        {sellingGuides.map((guide) => (
                            <div key={guide.id} className="guide-card">
                                <i className={`fas ${guide.icon}`}></i>
                                <h3>{guide.title}</h3>
                                <p>{guide.description}</p>
                                <Link to="#" className="guide-link">Learn More </Link>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Sell;
