import React, { useState, useEffect } from 'react';
import '../styles/Properties.css';

const Properties = () => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
        priceRange: '',
        propertyType: '',
        bedrooms: '',
        location: ''
    });

    useEffect(() => {
        fetchProperties();
    }, []);

    const fetchProperties = async () => {
        console.log('Fetching properties...');
        try {
            const response = await fetch('http://localhost:5001/api/properties');
            console.log('API Response:', response);
            if (!response.ok) {
                throw new Error('Failed to fetch properties');
            }
            const data = await response.json();
            console.log('Fetched properties:', data);
            setProperties(data.properties || []);
            setError(null);
        } catch (err) {
            console.error('Detailed error:', err);
            setError('Error fetching properties: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (propertyId) => {
        if (!window.confirm('Are you sure you want to delete this property?')) {
            return;
        }

        try {
            // First, login as admin to get the token
            const loginResponse = await fetch('http://localhost:5001/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: 'admin@example.com',
                    password: 'admin123'
                })
            });

            if (!loginResponse.ok) {
                throw new Error('Failed to authenticate');
            }

            const loginData = await loginResponse.json();
            const token = loginData.token;

            // Delete the property
            const deleteResponse = await fetch(`http://localhost:5001/api/properties/${propertyId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!deleteResponse.ok) {
                throw new Error('Failed to delete property');
            }

            // Remove the property from the state
            setProperties(prevProperties => 
                prevProperties.filter(property => property._id !== propertyId)
            );

            alert('Property deleted successfully!');
        } catch (err) {
            console.error('Error deleting property:', err);
            alert('Failed to delete property: ' + err.message);
        }
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const filteredProperties = properties.filter(property => {
        return (
            (!filters.priceRange || property.price <= parseInt(filters.priceRange)) &&
            (!filters.propertyType || property.type.toLowerCase() === filters.propertyType.toLowerCase()) &&
            (!filters.bedrooms || property.bedrooms >= parseInt(filters.bedrooms)) &&
            (!filters.location || property.location.toLowerCase().includes(filters.location.toLowerCase()))
        );
    });

    return (
        <div className="properties-page">
            <section className="search-section">
                <div className="search-container">
                    <h2>Find Your Perfect Property</h2>
                    <div className="filters">
                        <select
                            name="priceRange"
                            value={filters.priceRange}
                            onChange={handleFilterChange}
                        >
                            <option value="">Price Range</option>
                            <option value="500000">Under $500,000</option>
                            <option value="750000">Under $750,000</option>
                            <option value="1000000">Under $1,000,000</option>
                            <option value="1500000">Under $1,500,000</option>
                        </select>
                        <select
                            name="propertyType"
                            value={filters.propertyType}
                            onChange={handleFilterChange}
                        >
                            <option value="">Property Type</option>
                            <option value="house">House</option>
                            <option value="apartment">Apartment</option>
                            <option value="villa">Villa</option>
                            <option value="penthouse">Penthouse</option>
                            <option value="cabin">Cabin</option>
                        </select>
                        <select
                            name="bedrooms"
                            value={filters.bedrooms}
                            onChange={handleFilterChange}
                        >
                            <option value="">Bedrooms</option>
                            <option value="1">1+</option>
                            <option value="2">2+</option>
                            <option value="3">3+</option>
                            <option value="4">4+</option>
                            <option value="5">5+</option>
                        </select>
                        <input
                            type="text"
                            name="location"
                            placeholder="Location"
                            value={filters.location}
                            onChange={handleFilterChange}
                        />
                    </div>
                </div>
            </section>

            <section className="properties-section">
                <div className="properties-grid">
                    {loading ? (
                        <div className="loading-message">Loading properties...</div>
                    ) : error ? (
                        <div className="error-message">{error}</div>
                    ) : filteredProperties.length === 0 ? (
                        <div className="no-properties">No properties found matching your criteria.</div>
                    ) : (
                        filteredProperties.map(property => (
                            <div key={property._id} className="property-card">
                                <div className="property-image">
                                    <img 
                                        src={property.images?.[0] ? `http://localhost:5001${property.images[0]}` : '/images/property1.jpg'} 
                                        alt={property.title}
                                        onError={(e) => {
                                            e.target.onerror = null;
                                            e.target.src = '/images/property1.jpg';
                                        }}
                                    />
                                    <div className="property-price">
                                        ${property.price.toLocaleString()}
                                    </div>
                                    <div className="property-type">
                                        {property.type}
                                    </div>
                                </div>
                                <div className="property-details">
                                    <h3>{property.title}</h3>
                                    <p className="location">{property.location}</p>
                                    <p className="description">{property.description}</p>
                                    <div className="property-specs">
                                        <span>{property.bedrooms} Beds</span>
                                        <span>{property.bathrooms} Baths</span>
                                        <span>{property.area} sqft</span>
                                    </div>
                                    <div className="property-features">
                                        {property.features.slice(0, 4).map((feature, index) => (
                                            <span key={index}>{feature}</span>
                                        ))}
                                    </div>
                                    <div className="property-actions">
                                        <button className="view-details">View Details</button>
                                        <button 
                                            className="delete-property"
                                            onClick={() => handleDelete(property._id)}
                                        >
                                            Delete Property
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </section>
        </div>
    );
};

export default Properties;
