import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from '../utils/axiosInstance';
import '../styles.css';

const Signup = () => {
  const navigate = useNavigate();
  const [data, setData] = useState({ name: '', email: '', password: '', terms: false });

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!data.terms) return alert("Agree to terms!");
    try {
      const res = await axios.post('/auth/register', data);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Error');
    }
  };

  return (
    <div className="container">
      <h1>Create account</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" value={data.name} onChange={handleChange} placeholder="Name" required />
        <input name="email" type="email" value={data.email} onChange={handleChange} placeholder="Email" required/>
        <input name="password" type="password" value={data.password} onChange={handleChange} placeholder="Password" minLength="6" required/>
        <label>
          <input name="terms" type="checkbox" checked={data.terms} onChange={handleChange} required/>
          Agree to Terms
        </label>
        <button type="submit">Create account</button>
      </form>
      <hr />
      <button onClick={() => window.location.href = 'http://localhost:5001/api/auth/google'}>
        Sign up with Google
      </button>
      <button onClick={() => window.location.href = 'http://localhost:5001/api/auth/github'}>
        Sign up with GitHub
      </button>
      <p>Have an account? <Link to="/login">Login</Link></p>
    </div>
  );
};

export default Signup;
